import React from 'react'
import {
  sortableContainer,
  sortableElement,
  sortableHandle,
} from 'react-sortable-hoc'

import { arrayMoveImmutable } from 'array-move'
import { RiMenuLine, RiArrowRightSLine } from 'react-icons/ri'
import { Disclosure } from '@headlessui/react'

const DragHandle = sortableHandle(() => (
  <RiMenuLine className="w-6 h-6 m-2 text-gray-400" />
))

const FlatItem = sortableElement(({ value }) => (
  <div className="flex justify-start align-top">
    <li className="flex-col items-center justify-center list-none">
      <Disclosure>
        {({ open }) => (
          <>
            <Disclosure.Button className="flex w-[800px] items-center justify-between rounded-sm border bg-white px-4 py-1 text-left text-sm font-medium text-gray-500 hover:bg-primary-100 hover:text-white focus:outline-none focus-visible:ring focus-visible:ring-primary focus-visible:ring-opacity-75">
              <div className="flex items-center justify-center ">
                <DragHandle />
                <h1 className="text-lg font-bold">{value.name}</h1>
              </div>
              <RiArrowRightSLine
                className={`${
                  open ? 'rotate-90 transform' : ''
                } h-6 w-6 text-primary`}
              />
            </Disclosure.Button>
            <Disclosure.Panel className="px-4 pt-4 pb-2 text-gray-500 bg-gray-100">
              <NestedList />
            </Disclosure.Panel>
          </>
        )}
      </Disclosure>
    </li>
  </div>
))

const NestedItem = sortableElement(({ value }) => (
  <div className="flex items-center justify-between px-4 py-1 text-sm font-medium text-left text-gray-500 bg-white border rounded-sm hover:bg-primary-100 hover:text-white focus:outline-none focus-visible:ring focus-visible:ring-primary focus-visible:ring-opacity-75 ">
    <li className="flex items-center justify-center py-1 list-none">
      <DragHandle />
      <img
        className="object-center w-10 h-10 rounded-full"
        src={value.image}
        alt=""
      />
      <div className="ml-3">
        <p className="font-medium text-md ">{value.name}</p>
      </div>
    </li>
  </div>
))

const SortableContainer = sortableContainer(({ children }) => {
  return (
    <ul role="list" className="w-full">
      {children}
    </ul>
  )
})

